const cometd = require('cometd');

/**
 * Wrapper to open sockets
 *
 * @export
 * @class Session
 */
export default class Session {
  /**
   * Creates an instance of Session.
   *
   * @param {String} pin
   * @param {String} [proxy] - Optional cors proxy server url
   * @memberof Session
   */
  constructor(pin, proxy, protocol = 'https://') {
    this.pin = pin;
    this.proxy = proxy;
    this.protocol = protocol;
    this.web = new WebApi(proxy, protocol);
  }

  /**
   * Open a socket to the current game
   *
   * @returns {Promise<CometD>}
   * @memberof Session
   */
  openSocket() {
    return this.check(this.pin).then((info) => this.connect(info));
  }

  /**
   * Get session info
   *
   * @param {Number|String} pin
   * @returns {Promise<Object>} Game info
   * @memberof Session
   */
  async check(pin) {
    return http
      .get(`${this.proxy}${this.protocol}kahoot.it/reserve/session/${pin}/?${Helpers.time()}`)
      .then((response) => {
        const info = response.data;
        info.token = response.headers['x-kahoot-session-token'];
        info.pin = pin;

        return info;
      })
      .catch((error) => {
        if (error.message.includes('404')) {
          throw new Error('Game not found');
        } else {
          throw error;
        }
      });
  }

  /**
   * Open a socket using the game info provided by the check function
   *
   * @param {Object} info
   * @returns {Promise<CometD>}
   * @memberof Session
   */
  connect(info) {
    const socket = new cometd.CometD();
    const challenge = Helpers.solve(info.challenge);
    const session = Helpers.shiftBits(info.token, challenge);
    const containsInvalidChar = /[^A-Z,^a-z,^0-9]/g.test(session);
    if (containsInvalidChar) {
      return this.check(info.pin).then((secondInfo) => this.connect(secondInfo));
    }

    socket.configure({
      url: `https://kahoot.it/cometd/${info.pin}/${session}`,
    });
    socket.websocketEnabled = true;
    const handshake = new Promise((resolve) => socket.handshake(resolve));

    return handshake.then((response) => {
      if (!response.successful) throw new Error('Session failed to connect');
      socket.info = info;
      return socket;
    });
  }
}

import http from '../http';

export default class WebApi {
  constructor(proxy, protocol = 'https://') {
    this.endpoint = `${protocol}create.kahoot.it/rest`;
    this.proxy = proxy;

    this.accessToken = null;
  }

  /**
   * Authenticate to the web API with a username and password
   *
   * @param {String} username
   * @param {String} password
   * @returns {Promise<String>} - Access Token
   * @memberof WebApi
   */
  authenticate(username, password) {
    return http.post(`${this.proxy}${this.endpoint}/authenticate`, {
      grant_type: 'password',
      username,
      password,
    }).then((res) => {
      if (res.error) {
        throw new Error(res.error);
      } else if (res.access_token) {
        this.accessToken = res.access_token;
      }

      http.defaults.headers.common.Authorization = `Bearer ${this.accessToken}`;
      return this.accessToken;
    });
  }

  /**
   * Get kahoot info by uuid
   *
   * @param {String} uuid
   * @returns {Promise<Object>}
   * @memberof WebApi
   */
  getKahoot(uuid) {
    return http.get(`${this.proxy}${this.endpoint}/kahoots/${uuid}`).then((res) => res.data);
  }


  /**
   * Search for kahoots by name
   *
   * @param {String} query
   * @param {Number} [limit=25]
   * @returns {Promise<Object>}
   * @memberof WebApi
   */
  searchKahoots(query, limit = 25) {
    return http.get(`${this.proxy}${this.endpoint}/kahoots/?query=${query}&cursor=0&limit=${limit}&orderBy=relevance`)
      .then((res) => res.data);
  }
}

# Kahoot API

An API for the kahoot.it quiz platform. This release is intended for educational purposes only. The API is pretty outdated and I won't be supporting it any longer. It's still a good source for anyone looking to unravel the inner workings of Kahoot.

## Installation

Install with npm or yarn

```bash
yarn add kahoot-api
# or
npm install kahoot-api
```

## Usage

To get you started, here is a basic example of the Player authentication flow:

```JavaScript
import { Session, Adapters } from 'kahoot-api';

const session = new Session(000000 /** pin */, /** optional cors proxy, url string */)
session.openSocket() //Connect
  .then(socket => {
    const player = new Adapters.Player(socket); //Create player class
    player.join('test') //Join with name
      .then(() => {
         console.log('Success!');
      });
  });
```

For usage please refer to the [documentation](https://idiidk.app/kahoot-api)

